import { Component } from "react";


class Form extends Component {
constructor(props) {
  super(props);
  this.setState = {
  linkName: '',
  url: ''
  }

  this.handleChange = this.handleChange.bind(this);
  this.onFormChange = this.onFormChange.bind(this);
  }
  handleChange = event => {
  if(event.target.id === "link")
  this.setState({ linkname: event.target.value}); // update the linkname in state
  else
  this.setState({ url: event.target.value}); // update the url in state
  }
  onFormChange = event => {
  event.preventDefault();
  let newFavLink = {linkname: this.state.linkName, url: this.state.url};
  this.setState({ linkName: '', url: ''}); // reset the states
  this.props.addNew(newFabLink); // addNew was passed as props from LinkContainer
  }
  render() {
  return (
  <form>
  <label for="link">Link Name : </label>
  <input type="text" id="link" onChange={(event) =>this.handleChange(event)}/>
  <label for="url">URL : </label>
  <input type="text" id="url" onChange={(event) =>this.handleChange(event)}/>
  <button type="submit" onClick={() => this.onFormSubmit()}>Submit</button>
  </form>
  )
  }
}
export default Form;
